var spookyVision = (function(win) {
	var doc = win.document,
		body = doc.body,
		head = doc.getElementsByTagName('head')[0],
		winHeight = win.innerHeight || doc.documentElement.clientHeight,
		barbSize = winHeight * 0.15;

    function createStyle() {
		var zoominanim = ' {p}animation: zoomin 2s; ',
			rotateanim = '{p}animation: rotate 2s infinite;',
			zoominkf = ' @{p}keyframes zoomin { 0% { {p}transform: scale(0); } 100% { {p}transform: scale(1); } } \n',
			rotatekf = ' @{p}keyframes rotate { 0% { {p}transform: rotate(0deg); } 100% { {p}transform: rotate(360deg); } } \n',
			flyanim = '.spkvsn-fly { {p}transform: scale(10); {p}transition: all 1s ease; {p}transform-origin: top left; } \n' +
				'.spkvsn-fly.spkvsn-b2 { {p}transform-origin: top right; } \n' +
				'.spkvsn-fly.spkvsn-b3 { {p}transform-origin: bottom left; } \n' +
				'.spkvsn-fly.spkvsn-b4 { {p}transform-origin: bottom right; } \n',
			css = '.spkvsn-b {\n' +
			'position:absolute;width:' + barbSize + 'px;height:' + barbSize + 'px;' +
			prefixAll(zoominanim) +
			'\n}\n' +
			'.spkvsn-b:hover {\n' +
			prefixAll(rotateanim) +
			'\n}\n' +
			'.spkvsn-b1 {top:10px;left:10px} .spkvsn-b2{top:10px;right:10px} .spkvsn-b3{bottom:10px;left:10px} .spkvsn-b4{bottom:10px;right:10px}' +
			prefixAll(zoominkf) +
			prefixAll(rotatekf) +
			prefixAll(flyanim),
			style = doc.createElement('style');

		style.type = 'text/css';
		if (style.styleSheet){
			style.styleSheet.cssText = css;
		} else {
			style.appendChild(doc.createTextNode(css));
		}

		head.appendChild(style);
	}

	function prefixAll(css) {
		return prefixCss('-webkit-', css) +
			prefixCss('-moz-', css) +
			prefixCss('-ms-', css) +
			prefixCss('-o-', css) +
			prefixCss('', css);
	}

	function prefixCss(prefix, css) {
		return css.replace(/{p}/g, prefix);
	}

	function scaleText() {
		var spooky = doc.getElementById('spkvsn-l'),
			vision = doc.getElementById('spkvsn-r'),
			fontSize = 10,
			textHeight = winHeight - (barbSize * 2) - 100;

		spooky.style.fontSize = '10px';

		do {
			spooky.style.fontSize = (++fontSize) + 'px';
		} while(spooky.offsetHeight < textHeight);

		vision.style.fontSize = spooky.style.fontSize;
		vision.style.visibility = spooky.style.visibility = '';
	}
	
	function createElements() {
		var spkyLayer = doc.createElement('div'),
			barb = 'data:image/png;base64,',
			fs = 'visibility:hidden;position:absolute;text-align:center;color:red;font-family:Creepster;top:' + (barbSize + 30) + 'px;width:' + barbSize + 'px',
			img = '<img src="' + barb + '" class="spkvsn-b spkvsn-b§" />';
		spkyLayer.innerHTML = img.replace('§', 1) + img.replace('§', 2) + img.replace('§', 3) + img.replace('§', 4) + 
			'<div id="spkvsn-l" style="left:10px;'+ fs +'">S<br>P<br>O<br>O<br>K<br>Y</div>' + 
			'<div id="spkvsn-r" style="right:10px;' + fs + '">V<br>I<br>S<br>I<br>O<br>N</div>';
		spkyLayer.style.zIndex = 666666666;
		body.appendChild(spkyLayer);
	}

	function loadFont() {
		var wf = doc.createElement('link');
		wf.href = 'http://fonts.googleapis.com/css?family=Creepster';
	    wf.type = 'text/css';
		wf.rel = 'stylesheet';
	    head.appendChild(wf);
	}

	function setEvents() {
		var a = doc.getElementsByTagName('a'),
			l = a.length;

		while(l--) {
			addEvent(a[l], 'click', linkClickHandler);
		}
	}

	function addEvent(element, eventname, handler) {
		if(element.addEventListener) {
			element.addEventListener(eventname, handler, false);
		}
		else if(element.attachEvent) {
			element.attachEvent('on' + eventname, handler);
		}
		else {
			element['on' + eventname] = handler;
		}
	}

	function linkClickHandler(e) {
		e.preventDefault();

		var a = this,
			n = Math.floor((Math.random() * 4) + 1);
		
		doc.getElementsByClassName('spkvsn-b' + n)[0].className += ' spkvsn-fly';

		win.setTimeout(function() {
			location.href = a.href;
		}, 1000);
	}

	return function() {
		loadFont();
		createStyle();
		createElements();
		scaleText();
		setEvents();
	};
})(window);
